    $siteNavParent: $('#SiteNavParent'),
    $siteNavItem: $('.site-nav__item'),
    $siteNavSearchCart: $('#SiteNavSearchCart'),
    $drawerSearch: $('.drawer__search-input'),
    $drawerSearchSubmit: $('.drawer__search-submit'),

    $mainContent: $('#MainContent'),
    $stickyBar: $('#StickyBar'),
    $stickyNav: $('#StickyNav'),
    $stickNavWrapper: $('#StickNavWrapper'),
    $stickyNavSearchCart: $('#StickyNavSearchCart'),

    $sortDropdown: $('#SortBy'),
    $tagsDropdown: $('#SortTags'),

    $indentedRteImages: $('.rte--indented-images'),

    $addToCartForm: $('#AddToCartForm'),
    $addToCart: $('#AddToCart'),
    $productPrice: $('#ProductPrice'),
    $comparePrice: $('#ComparePrice'),
    $saleTag: $('#ProductSaleTag'),
    $productStock: $('#ProductStock'),
    $addToCartText: $('#AddToCartText'),
    $productImageModalTrigger: $('#ProductPhotoLink'),
    $productFeaturedImage: $('#ProductPhotoImg'),
    $productThumbs: $('#ProductThumbs'),
    $productThumbArrows: $('.product-single__arrow'),
