shop.init = function () {
  shop.initCache();
  shop.stringOverrides();
  shop.isLocalStorageSupported = shop.isLocalStorageSupported();
  shop.NavDrawer = new window.Drawers('NavDrawer', 'left');
  shop.initMeganavs();
  shop.cache.$numberInput.each(function (i, el) {
    new QtySelector($(el));
  });
  shop.initStickyHeader();
  shop.initHeaderSearch();
  shop.siteNavSizing();
  shop.heroInit();
  shop.initProductVariant();
  shop.productImageSwitch();
  shop.productZoomImage();
  shop.AjaxCart = new window.AjaxCart(shop.cache.$addToCartForm);
  shop.drawerSearch();
  shop.cartNote();
  shop.setQueryParams();
  shop.passwordModalInit();
  shop.responsiveVideos();
  shop.setActiveThumbnail();

  // Bind events
  shop.cache.$window.on('load', shop.articleImages);
  shop.cache.$window.on('load', shop.productThumbnailSlider);
  shop.cache.$window.on('scroll', $.throttle(15, shop.stickyHeaderOnScroll));
  shop.cache.$sortDropdown.on('change', shop.sortCollection);
  shop.cache.$tagsDropdown.on('change', shop.filterCollection);
};

shop.stringOverrides = function () {
  // Override defaults in shop.strings with potential
  // template overrides
  window.productStrings = window.productStrings || {};
  $.extend(shop.strings, window.productStrings);
};

shop.isLocalStorageSupported = function () {
  // Return false if we are in an iframe without access to sessionStorage
  if (window.self !== window.top) {
    return false;
  }

  var testKey = 'test';
  var storage = window.sessionStorage;
  try {
    storage.setItem(testKey, '1');
    storage.removeItem(testKey);
    return true;
  } catch (error) {
    return false;
  }
};

shop.initMeganavs = function() {
  shop.headerMeganavs = new window.Meganav({
    $meganavs: $('.site-nav__dropdown'),
    $meganavToggle: $('.site-nav__meganav-toggle'),
    preventDuplicates: true,
    closeOnPageClick: true
  });

  shop.indexMeganavs = new window.Meganav({
    $meganavs: $('.meganav--index'),
    $meganavToggle: $('.index__meganav-toggle')
  });

  shop.drawerMeganavs = new window.Meganav({
    $meganavs: $('.meganav--drawer'),
    $meganavToggle: $('.drawer__meganav-toggle')
  });
};

shop.initHeaderSearch = function () {
  // This function runs after the header search element
  // is duplicated into the sticky nav, meaning there are
  // two search fields to be aware of at this point.
  var $searchForm = $('.site-header__search');

  $searchForm.each(function (i, el) {
    var $form = $(el);
    var $input = $form.find('.' + shop.headerSearch.inputClass);
    var $submit = $form.find('.' + shop.headerSearch.submitClass);

    $input.on('focus', function() {
      $input.addClass(shop.headerSearch.searchShowClass);
    });

    $input.on('blur', function() {
      if ($input.val()) {
        $input.addClass(shop.headerSearch.searchShowClass);
      } else {
        $input.removeClass(shop.headerSearch.searchShowClass);
      }
    });

    $submit.on('click', function(evt) {
      if (!$input.hasClass(shop.headerSearch.searchShowClass)) {
        evt.preventDefault();
        $input.addClass(shop.headerSearch.searchShowClass).focus();
      }
    });
  });
};

shop.drawerSearch = function () {
  shop.cache.$drawerSearchSubmit.on('click', function(evt) {
    if (shop.cache.$drawerSearch.val().length == 0) {
      evt.preventDefault();
      shop.cache.$drawerSearch.focus();
    }
  });
};

shop.siteNavSizing = function () {
  shop.sizeNav();
  shop.cache.$window.on('resize', $.debounce(250, shop.sizeNav));
};

shop.heroInit = function () {
  if (!shop.cache.$hero.length) {
    return;
  }

  shop.cache.$hero.on('init', shop.heroA11y);
  shop.cache.$hero.on('init', shop.heroArrowsInit);

  shop.cache.$hero.slick({
    accessibility  : true,
    arrows         : false, // this theme has custom arrows
    dots           : false,
    draggable      : false,
    autoplay       : shop.cache.$hero.data('autoplay'),
    autoplaySpeed  : shop.cache.$hero.data('speed')
  });

  shop.cache.$heroImage.on('click', function() {
    shop.cache.$hero.slick('slickNext');
  });

  shop.cache.$heroPause.on('click', function() {
    if ($(this).hasClass('is-paused')) {
      shop.heroPlay();
    } else {
      shop.heroPause();
    }
  });
};

shop.heroPause = function () {
  shop.cache.$hero.slick('slickPause');
  shop.cache.$heroPause.addClass('is-paused');
};

shop.heroPlay = function () {
  shop.cache.$hero.slick('slickPlay');
  shop.cache.$heroPause.removeClass('is-paused');
};

shop.heroA11y = function (evt, obj) {
  var $slider = obj.$slider;
  var $list = obj.$list;

  // Remove default Slick aria-live attr until slider is focused
  $list.removeAttr('aria-live');

  // When an element in the slider is focused
  // pause slideshow and set aria-live
  shop.cache.$heroWrapper.on('focusin', function(evt) {
    if (shop.cache.$heroWrapper.has(evt.target).length) {
      $list.attr('aria-live', 'polite');
      shop.heroPause();
    }
  });

  // Resume autoplay
  shop.cache.$heroWrapper.on('focusout', function(evt) {
    if (shop.cache.$heroWrapper.has(evt.target).length) {
      $list.removeAttr('aria-live');
      shop.heroPlay();
    }
  });
};

shop.heroArrowsInit = function (evt, obj) {
  // Slider is initialized. Setup custom arrows
  var count = obj.slideCount;
  var $slider = obj.$slider;
  var $arrows = shop.cache.$heroArrows;

  if ($arrows.length && count > 1) {
    $arrows.on('click', function (evt) {
      evt.preventDefault();
      if ($(this).hasClass('hero__arrow--prev')) {
        $slider.slick('slickPrev');
      } else {
        $slider.slick('slickNext');
      }

      shop.heroScrollTop();
    });
  } else {
    $arrows.remove()
  }
};

shop.heroScrollTop = function () {
  var currentScroll = shop.cache.$document.scrollTop();
  var heroOffset = shop.cache.$hero.offset().top;

  if (currentScroll > heroOffset) {
    shop.cache.$scrollParent.animate({
      scrollTop: heroOffset
    }, 250);
  }
};

shop.sizeNav = function () {
  var navWidth = 0;
  var parentWidth = shop.cache.$siteNavParent.width();
  var hideClass = 'hide';

  // Set height on nav-bar wrapper so when fixed, doesn't break layout
  shop.cache.$stickNavWrapper.height(shop.cache.$stickyNav.height());

  // Calculate the width of each nav item
  // after forcing them to be visible for the calculations
  shop.cache.$siteNav.removeClass(hideClass);
  shop.cache.$siteNavItem.each(function (i, el){
    navWidth += $(el).width();
  });

  if (navWidth > parentWidth) {
    shop.cache.$siteNav.addClass(hideClass);
    shop.cache.$siteNavCompressed.removeClass(hideClass);
  } else {
    shop.cache.$siteNav.removeClass(hideClass);
    shop.cache.$siteNavCompressed.addClass(hideClass);
  }
};

shop.initStickyHeader = function() {
  // Clone search/cart icons into fixed nav
  if (shop.cache.$siteNavSearchCart.contents().length) {
    shop.cache.$siteNavSearchCart.contents().clone().appendTo(shop.cache.$stickyNavSearchCart)
  };
};

shop.stickyHeaderOnScroll = function (evt) {
  var scroll = shop.cache.$window.scrollTop();
  var $el = shop.cache.$stickNavWrapper;
  var threshold = $el.offset().top + $el.height() + 10; // default for scrolling down

  // Check if scrolling up
  if (scroll < shop.stickyNav.lastScroll) {
    threshold = $el.offset().top
  }

  if(scroll > threshold) {
    shop.stickNav();
  } else {
    shop.unstickNav();
  }

  shop.stickyNav.lastScroll = scroll;
}

shop.stickNav = function () {
  var $sticky = shop.cache.$stickyBar;

  if (shop.stickNav.isActive) {
    return;
  }

  shop.stickNav.isActive = true;

  $sticky.addClass(shop.stickyNav.navClass);

  // Add open transition class after element is set to fixed
  // so CSS animation is applied correctly
  setTimeout(function () {
    $sticky.addClass(shop.stickyNav.openTransitionClass);
  }, 0);
};

shop.unstickNav = function () {
  if (!shop.stickNav.isActive) {
    return;
  }

  shop.cache.$stickyBar.removeClass(shop.stickyNav.openTransitionClass).removeClass(shop.stickyNav.navClass);

  shop.stickNav.isActive = false;
};

shop.articleImages = function () {
  if (!shop.cache.$indentedRteImages.length) {
    return;
  }

  shop.cache.$indentedRteImages.find('img').each(function (i, el) {
    var $el = $(el);
    var attr = $el.attr('style');

    // Check if undefined or float: none
    if (!attr || attr == 'float: none;') {
      // Add class to parent paragraph tag if image is wider than container
      if ($el.width() >= shop.cache.$indentedRteImages.width()) {
        $el.parent('p').addClass('rte__image-indent');
      }
    }
  });
};

shop.setQueryParams = function  () {
  // Set current sort by value
  if (shop.cache.$sortDropdown.length && Shopify.queryParams.sort_by) {
    shop.cache.$sortDropdown.val(Shopify.queryParams.sort_by);
  }
};

shop.sortCollection = function () {
  Shopify.queryParams.sort_by = shop.cache.$sortDropdown.val();
  location.search = jQuery.param(Shopify.queryParams);
};

shop.filterCollection = function () {
  if (!shop.cache.$tagsDropdown.length) {
    return;
  }

  window.location.href = shop.cache.$tagsDropdown.val() + '?' + jQuery.param(Shopify.queryParams);
};

shop.initProductVariant = function () {
  // productSingleObject is a global JSON object defined in product.liquid
  if (!shop.productSingleObject) {
    return;
  }

  // Initialize multiple variant selector dropdowns
  new Shopify.OptionSelectors('ProductSelect', {
    product: shop.productSingleObject,
    onVariantSelected: shop.productVariantCallback,
    enableHistoryState: true
  });

  // option_selection.js does not add a label if there is only one variant option.
  // Add one as long as it is not 'Title' (the Shopify default), add one.
  if (shop.productSingleObject.options.length === 1 && shop.productSingleObject.options[0] !== 'Title') {
    $('.selector-wrapper:eq(0)').prepend('<label for="ProductSelect-option-0">'+ shop.productSingleObject.options[0] +'</label>');
  }

  // Hide variant dropdown if only one exists and title contains 'Default'
  if (shop.productSingleObject.variants.length && shop.productSingleObject.variants[0].title.indexOf('Default') >= 0) {
    $('.selector-wrapper').hide();
  }

  shop.productVariantLayout();
};

shop.productVariantLayout = function () {
  var $variantWrappers = $('.selector-wrapper').addClass('product-form__item');
  var $variantInputs = $('.single-option-selector').addClass('product-form__input');
};

shop.switchImage = function (image, largeImage) {
  shop.cache.$productFeaturedImage.attr('src', image);
  shop.cache.$productImageModalTrigger.attr('href', largeImage);
};

shop.productThumbnailSlider = function () {
  if (!shop.cache.$productThumbs.length) {
    return;
  }

  var thumbnailCount = shop.cache.$productThumbs.find('.product-single__thumbnail').length;

  // Only init slider if 3+ products
  if (thumbnailCount > 2) {
    shop.cache.$productThumbs.on('init', shop.productSwipeInit);

    // Next/Prev arrow markup, for both vertical/horizontal layouts
    var nextArrow = '<button type="button" class="slick-next"><span class="medium-up--hide"><svg class="icon icon-arrow-right"><use xlink:href="#icon-arrow-right"></use></svg></span><span class="small--hide"><svg class="icon icon-arrow-down"><use xlink:href="#icon-arrow-down"></use></svg></span></button>';
    var prevArrow = '<button type="button" class="slick-prev"><span class="medium-up--hide"><svg class="icon icon-arrow-left"><use xlink:href="#icon-arrow-left"></use></svg></span><span class="small--hide"><svg class="icon icon-arrow-up"><use xlink:href="#icon-arrow-up"></use></svg></span></button>';

    shop.cache.$productThumbs.slick({
      accessibility: false,
      arrows: true,
      dots: false,
      infinite: false,
      autoplay: false,
      slidesToShow: 3,
      slidesToScroll: 3,
      vertical: true,
      verticalSwiping: true,
      nextArrow: nextArrow,
      prevArrow: prevArrow,
      responsive: [
        {
          breakpoint: shop.settings.smallBreakpoint,
          settings: {
            vertical: false,
            verticalSwiping: false
          }
        }
      ]
    });
  }
};

shop.productSwipeInit = function (evt, obj) {
  // Slider is initialized. Setup custom swipe events
  shop.productThumbs.index = obj.currentSlide;
  shop.productThumbs.max = obj.slideCount - 1; // we need the 0-based index

  shop.cache.$productFeaturedImage.on('swipeleft swiperight', function(evt) {
    if (evt.type === 'swipeleft') {
      shop.goToNextThumbnail();
    }

    if (evt.type === 'swiperight') {
      shop.goToPrevThumbnail();
    }

    // Trigger click on newly requested thumbnail
    $('.product-single__thumbnail-item[data-slick-index="' + shop.productThumbs.index + '"]').find('.product-single__thumbnail').trigger('click');
  });
};

shop.productImageSwitch = function () {
  if (!shop.cache.$productThumbs.length) {
    return;
  }

  shop.cache.$productThumbs.on('click', '.product-single__thumbnail', function (evt) {
    evt.preventDefault();
    var $el = $(this);
    var sizedImageUrl = $el.attr('href');
    var largeImageUrl = $el.attr('data-zoom');
    shop.setActiveThumbnail($el);
    shop.switchImage(sizedImageUrl, largeImageUrl);
  });
};

shop.productZoomImage = function () {
  if (!shop.cache.$productFeaturedImage.length) {
    return;
  }

  shop.cache.$productImageModalTrigger.on('click', function(evt) {
    // Empty src before loadig new image to avoid awkward image swap
    shop.cache.$productZoomImage
      .attr('src', '')
      .attr('src', $(this).attr('href'));
  });

  shop.ProductModal = new window.Modals('ProductModal', 'product-modal');

  // Close modal if clicked, but not if the image is clicked
  shop.ProductModal.$modal.on('click', function(evt) {
    if (evt.target.nodeName !== 'IMG') {
      shop.ProductModal.close();
    }
  });
};

shop.goToNextThumbnail = function () {
  shop.productThumbs.index++;

  if (shop.productThumbs.index > shop.productThumbs.max) {
    shop.productThumbs.index = 0;
  }

  shop.cache.$productThumbs.slick('slickGoTo', shop.productThumbs.index, true);
};

shop.goToPrevThumbnail = function () {
  shop.productThumbs.index--;

  if (shop.productThumbs.index < 0) {
    shop.productThumbs.index = shop.productThumbs.max;
  }

  shop.cache.$productThumbs.slick('slickGoTo', shop.productThumbs.index, true);
};

shop.setActiveThumbnail = function ($el) {
  // If there is no element passed, find it by the current product image
  if (typeof $el === 'undefined') {
    var src = shop.cache.$productFeaturedImage.attr('src');
    $el = $('.product-single__thumbnail[href="'+ src +'"]');
  }

  // Set active thumbnail classes (on slick-generated slides)
  shop.cache.$productThumbs.find('.product-single__thumbnail-item').removeClass('is-active');
  $el.parent().addClass('is-active');
};

shop.productVariantCallback = function (variant, selector) {
  if (variant) {
    // Update variant image, if one is set
    if (variant.featured_image) {
      var size = Shopify.Image.imageSize(shop.cache.$productFeaturedImage.attr('src'));
      var sizedImgUrl = Shopify.Image.getSizedImageUrl(variant.featured_image.src, size);
      var largeImgUrl = Shopify.Image.getSizedImageUrl(variant.featured_image.src, '1024x1024')
      shop.switchImage(sizedImgUrl, largeImgUrl);
      shop.setActiveThumbnail();
    }

    // Update the product price
    shop.cache.$productPrice.html(Shopify.formatMoney(variant.price, shop.moneyFormat));

    // Update and show the product's compare price if necessary
    if (variant.compare_at_price > variant.price) {
      shop.cache.$comparePrice.html(Shopify.formatMoney(variant.compare_at_price, shop.moneyFormat)).removeClass('hide');
      shop.cache.$saleTag.removeClass('hide');
    } else {
      shop.cache.$comparePrice.addClass('hide');
      shop.cache.$saleTag.addClass('hide');
    }

    // Select a valid variant if available
    if (variant.available) {
      // We have a valid product variant, so enable the submit button
      shop.cache.$addToCart.removeClass('btn--sold-out').prop('disabled', false);
      shop.cache.$addToCartText.html(shop.strings.addToCart);

      // Show how many items are left, if below 10
      if (shop.settings.productStockSetting) {
        if (variant.inventory_management) {
          if (variant.inventory_quantity < 10 && variant.inventory_quantity > 0) {
            shop.cache.$productStock.html(shop.strings.stockAvailable.replace('1', variant.inventory_quantity)).removeClass('hide');
          } else {
            shop.cache.$productStock.addClass('hide');
          }
        } else {
          shop.cache.$productStock.addClass('hide');
        }
      }
    } else {
      // Variant is sold out, disable the submit button
      shop.cache.$addToCart.prop('disabled', true).addClass('btn--sold-out');
      shop.cache.$addToCartText.html(shop.strings.soldOut);
      if (shop.settings.productStockSetting) {
        shop.cache.$productStock.addClass('hide');
      }
    }
  } else {
    // The variant doesn't exist, disable submit button.
    // This may be an error or notice that a specific variant is not available.
    shop.cache.$addToCart.prop('disabled', true);
    shop.cache.$addToCartText.html(shop.strings.unavailable);
    if (shop.settings.productStockSetting) {
      shop.cache.$productStock.addClass('hide');
    }
  }
};

shop.cartNote = function () {
  if (!shop.cache.$cartNote.length) {
    return;
  }

  var $el = shop.cache.$cartNote;
  var noteText;
  var params;
  var noteOffset = $el[0].offsetHeight - $el[0].clientHeight;

  // Auto grow the cart note if text fills it up
  $el.on('keyup input', function(evt) {
    shop.resizeTextarea($(this), noteOffset);
  });

  // Save the cart note via ajax. A safeguard in case
  // a user decides to leave the page before clicking 'Update Cart'
  $el.on('change', $.proxy(function () {
    noteText = $el.val();
    params = {
      type: 'POST',
      url: '/cart/update.js',
      data: 'note=' + shop.attributeToString(noteText),
      dataType: 'json'
    };
    $.ajax(params);
  }, this));
};

shop.resizeTextarea = function ($el, offset) {
  $el.css('height', 'auto').css('height', $el[0].scrollHeight + offset);
};

shop.passwordModalInit = function () {
  if (!shop.cache.$loginModal.length) {
    return;
  }

  // Initialize modal
  shop.PasswordModal = new window.Modals('LoginModal', 'login-modal', {
    focusOnOpen: '#Password'
  });

  // Open modal if errors exist
  if (shop.cache.$loginModal.find('.errors').length) {
    shop.PasswordModal.open();
  }
};

shop.responsiveVideos = function () {
  var $iframeVideo = $('iframe[src*="youtube.com/embed"], iframe[src*="player.vimeo"]');
  var $iframeReset = $iframeVideo.add('iframe#admin_bar_iframe');

  $iframeVideo.each(function () {
    // Add wrapper to make video responsive
    $(this).wrap('<div class="video-wrapper"></div>');
  });

  $iframeReset.each(function () {
    // Re-set the src attribute on each iframe after page load
    // for Chrome's "incorrect iFrame content on 'back'" bug.
    // https://code.google.com/p/chromium/issues/detail?id=395791
    // Need to specifically target video and admin bar
    this.src = this.src;
  });
};

shop.attributeToString = function (attr) {
  if ((typeof attr) !== 'string') {
    attr += '';
    if (attr === 'undefined') {
      attr = '';
    }
  }
  return $.trim(attr);
};

$(shop.init);
